package br.com;import br.com.config.App;import br.com.collection.NanoCollection;import br.com.input.InputFile;import br.com.sort.BubbleParallelSort;import java.io.BufferedReader;import java.io.File;import java.io.FileReader;import java.io.IOException;public class Main {    public static void main(String args[]) throws IOException, InterruptedException {        NanoCollection<Integer> list = new NanoCollection<>();        InputFile inputDataFile = new InputFile();        inputDataFile.startUp(10);        for (int i = 1; i <= App.THREAD_NUMBER; i++) {            File fileReader = new File(App.folder() + App.FILE + i);            BufferedReader objReader = new BufferedReader(new FileReader(fileReader));            new Thread(() -> {                try {                    String strCurrentLine;                    while ((strCurrentLine = objReader.readLine()) != null) {                        synchronized (list) {                            list.add(Integer.valueOf(strCurrentLine));                        }                    }                } catch (IOException e) {                    e.printStackTrace();                }            }).start();        }        Thread.sleep(1000);        for (int i = 0; i < 1; i++)            new Thread(new BubbleParallelSort(list)).start();    }    public static void print_list(NanoCollection<Integer> list) {        for (Integer i : list) {            System.out.println(i);        }    }}